package com.training.example;

// TODO: Auto-generated Javadoc
/**
 * The Class Calc.
 */
public class Calc {

	/**
	 * Adds the.
	 *
	 * @param i the i
	 * @param j the j
	 * @return the int
	 */
	public int add(int i, int j){
		return i+j;
	}
	
	/**
	 * Multiply.
	 *
	 * @param i the i
	 * @param j the j
	 * @return the int
	 */
	public int multiply(int i, int j){
		return i*j;
	}

}
